SECTION.CODE:

def show_page
    @contacts = Contact.find(
        :all,
        :conditions => {
        {
            :func => 'LOWER',
            :name => 'FirstName',
            :op => 'LIKE'
        } => @params[:FirstName],
        {
            :func => 'LOWER',
            :name=>'LastName',
            :op=>'LIKE'
        } => @params[:LastName],
        {
            :func=>'LOWER',
            :name=>'Company',
            :op=>'LIKE'
        } => @params[:Company],
        },
        :op => 'OR',
        :select => ['FirstName','LastName', 'Company'],
        :per_page => page_size,
        :offset => page * page_size
    )
    render :action => :show_page
end
